{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"pygments_lexer":"ipython3","nbconvert_exporter":"python","version":"3.6.4","file_extension":".py","codemirror_mode":{"name":"ipython","version":3},"name":"python","mimetype":"text/x-python"}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"# %% [code] {\"id\":\"DM4jd7SEHjKf\",\"jupyter\":{\"outputs_hidden\":false},\"execution\":{\"iopub.status.busy\":\"2021-10-07T16:32:20.960917Z\",\"iopub.execute_input\":\"2021-10-07T16:32:20.961301Z\",\"iopub.status.idle\":\"2021-10-07T16:32:20.978797Z\",\"shell.execute_reply.started\":\"2021-10-07T16:32:20.961188Z\",\"shell.execute_reply\":\"2021-10-07T16:32:20.977650Z\"}}\nimport pandas as pd\nimport numpy as np\nimport matplotlib.pyplot as plt\nimport os\nfrom PIL import Image\nimport plotly.graph_objects as go\nimport gc\n\n# %% [code] {\"id\":\"STGzMwAnSnlT\",\"jupyter\":{\"outputs_hidden\":false},\"execution\":{\"iopub.status.busy\":\"2021-10-07T16:32:20.980764Z\",\"iopub.execute_input\":\"2021-10-07T16:32:20.981178Z\",\"iopub.status.idle\":\"2021-10-07T16:33:13.915936Z\",\"shell.execute_reply.started\":\"2021-10-07T16:32:20.981142Z\",\"shell.execute_reply\":\"2021-10-07T16:33:13.914895Z\"}}\n#Density\n#for time_stamp in range(200):#As there are 200 time stamps in total.\ntime_stamp = 119\nfile = str(time_stamp).zfill(4)#As the file numbers in the data are in the form 0000 to 0199. \ndata_files = pd.read_csv('../input/dv-data-1/multifield.'+ file + '.txt', delimiter=' ',header=None)\ntotal_partical_density = data_files[0].values #Selecting first scalar value to visualize to be density\n\n# %% [code] {\"jupyter\":{\"outputs_hidden\":false},\"execution\":{\"iopub.status.busy\":\"2021-10-07T16:33:13.917502Z\",\"iopub.execute_input\":\"2021-10-07T16:33:13.917872Z\",\"iopub.status.idle\":\"2021-10-07T16:33:13.923072Z\",\"shell.execute_reply.started\":\"2021-10-07T16:33:13.917830Z\",\"shell.execute_reply\":\"2021-10-07T16:33:13.922045Z\"}}\n#Plane equation\nA =4.0\nB =6.0\nC =-8.0\n\n# %% [code] {\"jupyter\":{\"outputs_hidden\":false},\"execution\":{\"iopub.status.busy\":\"2021-10-07T16:33:13.924418Z\",\"iopub.execute_input\":\"2021-10-07T16:33:13.924645Z\",\"iopub.status.idle\":\"2021-10-07T16:33:13.937431Z\",\"shell.execute_reply.started\":\"2021-10-07T16:33:13.924620Z\",\"shell.execute_reply\":\"2021-10-07T16:33:13.936150Z\"}}\nx = np.arange(600)\ny = np.arange(248)\n\n# %% [code] {\"jupyter\":{\"outputs_hidden\":false},\"execution\":{\"iopub.status.busy\":\"2021-10-07T16:33:13.939899Z\",\"iopub.execute_input\":\"2021-10-07T16:33:13.940156Z\",\"iopub.status.idle\":\"2021-10-07T16:33:13.953681Z\",\"shell.execute_reply.started\":\"2021-10-07T16:33:13.940127Z\",\"shell.execute_reply\":\"2021-10-07T16:33:13.952383Z\"}}\ny,x = np.meshgrid(y,x)\nprint(x.shape)\nprint(y.shape)\n\n# %% [code] {\"jupyter\":{\"outputs_hidden\":false},\"execution\":{\"iopub.status.busy\":\"2021-10-07T16:33:13.955116Z\",\"iopub.execute_input\":\"2021-10-07T16:33:13.955557Z\",\"iopub.status.idle\":\"2021-10-07T16:33:13.967088Z\",\"shell.execute_reply.started\":\"2021-10-07T16:33:13.955523Z\",\"shell.execute_reply\":\"2021-10-07T16:33:13.966110Z\"}}\ndef get_val(x,y,z):\n    z = int(z)\n    id = x + 600*y + 248*600*z\n    return total_partical_density[id]\n\n# %% [code] {\"jupyter\":{\"outputs_hidden\":false},\"execution\":{\"iopub.status.busy\":\"2021-10-07T16:33:13.968530Z\",\"iopub.execute_input\":\"2021-10-07T16:33:13.969407Z\",\"iopub.status.idle\":\"2021-10-07T16:33:13.977958Z\",\"shell.execute_reply.started\":\"2021-10-07T16:33:13.969369Z\",\"shell.execute_reply\":\"2021-10-07T16:33:13.977175Z\"}}\ndef get_the_slice(x,y,z,surfacecolor):\n    return go.Surface(x=x,y=y,z=z,surfacecolor=surfacecolor)\ndef get_lims_colors(surfacecolor): # color limits for a slice\n    return np.min(surfacecolor), np.max(surfacecolor)\n\n# %% [code] {\"jupyter\":{\"outputs_hidden\":false},\"execution\":{\"iopub.status.busy\":\"2021-10-07T16:33:13.978963Z\",\"iopub.execute_input\":\"2021-10-07T16:33:13.979726Z\",\"iopub.status.idle\":\"2021-10-07T16:33:17.706643Z\",\"shell.execute_reply.started\":\"2021-10-07T16:33:13.979686Z\",\"shell.execute_reply\":\"2021-10-07T16:33:17.704705Z\"}}\nfor D in range(0,30000000,5000000):\n    z=(-1)*(D + A*x + B*y)/C\n    print(z.shape)\n    \n    val = np.zeros_like(z)\n    print(val.shape)\n    \n    for i in range(600):\n        for j in range(248):        \n            if z[i,j]<0 or z[i,j]>=248:\n                val[i,j] = 0\n            else:\n                val[i,j] = get_val(i,j,z[i,j])\n                \n    sminz, smaxz = get_lims_colors(val)\n    slice_z = get_the_slice(x,y,z,val)\n    \n    fig = go.Figure ( data=slice_z )\n    fig.show()","metadata":{"_uuid":"b9c9698a-a8e8-4a9e-895a-70a39d86008c","_cell_guid":"0f85b024-310c-4f4c-aff4-61a574a2ba86","collapsed":false,"jupyter":{"outputs_hidden":false},"trusted":true},"execution_count":null,"outputs":[]}]}